<?phpclass AccessControlModel extends Model{		/* 智能门禁展示选项	* village_id	小区ID	* @time 2016-06-8	* @author	小邓  <969101097@qq.com>*/	public function getAccesscontrollist($village_id=''){				$condition_table=array(			'pigcms_access_control_group'=>'group',			'pigcms_access_control'=>'control'		);		$condition_where='group.ag_id=control.ag_id and control.ac_status=1 and control.village_id='.$village_id;		$condition_field='group.ag_name,control.ac_id,control.ag_id,control.ac_name,control.apikey,control.nodeid,control.sensorid';		$access_control_result=M('')->table($condition_table)->where($condition_where)->field($condition_field)->select();		return $access_control_result;	}	}?>