<?phpclass PayModel extends Model{	/*根据 支付平台的英文 和 是否移动端支付 得到中文名称*/	public function get_pay_name($pay_type,$is_mobile_pay){		switch($pay_type){			case 'alipay':				$pay_type_txt = '支付宝';				break;			case 'tenpay':				$pay_type_txt = '财付通';				break;			case 'yeepay':				$pay_type_txt = '易宝支付';				break;			case 'allinpay':				$pay_type_txt = '通联支付';				break;			case 'chinabank':				$pay_type_txt = '网银在线';				break;			case 'weixin':				$pay_type_txt = '微信支付';				break;			case 'wxcode':				$pay_type_txt = '扫码支付';				break;			case 'balance':				$pay_type_txt = '会员卡余额支付';				break;			case 'now_money':				$pay_type_txt = '账户余额支付';				break;			case 'hy_ac':				$pay_type_txt = '余额支付（会员、余额）';				break;			case 'hy_ac_wx':				$pay_type_txt = '混合支付（会员、余额、微信）';				break;			case 'hy_wx':				$pay_type_txt = '混合支付（会员、微信）';				break;			case 'ac_wx':				$pay_type_txt = '混合支付（余额、微信）';				break;			case 'offline':				$pay_type_txt = '货到付款';				break;			default:				$pay_type_txt = '余额支付';			case 'micropay':				$pay_type_txt = '刷卡支付';				break;		}		if($is_mobile_pay){			$pay_type_txt .= '(移动端)';		}		return $pay_type_txt;	}    public function get_pay_name2($pay_type){	    $pay_names = array(	        'alipay'=>'支付宝',	        'tenpay'=>'财付通',	        'yeepay'=>'易宝支付',	        'allinpay'=>'通联支付',	        'chinabank'=>'网银在线',	        'weixin'=>'微信支付',	        'wxcode'=>'扫码支付',	        'balance'=>'会员卡余额支付',	        'now_money'=>'账户余额支付',	        'hy_ac'=>'余额支付（会员、余额 )',	        'hy_ac_wx'=>'混合支付（会员、余额、微信）',	        'hy_wx'=>'混合支付（会员、微信）',	        'ac_wx'=>'混合支付（余额、微信）',	        'offline'=>'货到付款',	        'micropay'=>'刷卡支付',        );        if(empty($pay_type)){            return $pay_names;        }else{            return $pay_names[$pay_type];        }    }}?>