<include file="Public:header"/><div class="main-content">	<!-- 内容头部 -->	<div class="breadcrumbs" id="breadcrumbs">		<ul class="breadcrumb">			<li>				<i class="ace-icon fa fa-tablet"></i>				<a href="{pigcms{:U('User/index')}">业主列表</a>			</li>			<li class="active">手动添加业主</li>		</ul>	</div>	<!-- 内容头部 -->	<div class="page-content">		<div class="page-content-area">			<style>				.ace-file-input a {display:none;}			</style>			<div class="row">				<div class="col-xs-12">					<form  class="form-horizontal" method="post" id="edit_form" action="__SELF__">					    <input type="hidden" name="company_id" value=""/>						<div class="tab-content">							<div id="basicinfo" class="tab-pane active">								<div class="form-group">									<label class="col-sm-1"><label for="cat_name">公司名称</label></label>									<input class="col-sm-2" size="20" name="company_name"  type="text"/>								</div>								<div class="form-group">									<label class="col-sm-1"><label for="phone">公司负责人姓名</label></label>									<input class="col-sm-2" size="20" name="name" type="text" />								</div>                                <div class="form-group">                                    <label class="col-sm-1"><label for="phone">联系手机号</label></label>                                    <input class="col-sm-2" size="20" name="phone" type="text"  />                                </div>                                <div class="form-group" style="display: none">                                    <label class="col-sm-1"><label for="phone">楼层号</label></label>                                    <input class="col-sm-2" size="20" name="floor" type="text" />                                </div>                                <div class="form-group">                                    <label class="col-sm-1"><label for="phone">房屋面积</label></label>                                    <input class="col-sm-2" size="20" name="housesize" type="text" />                                </div>                                <div class="form-group">                                    <label class="col-sm-1"><label for="phone">水费欠费</label></label>                                    <input class="col-sm-2" size="20" name="water_price" type="text" placeholder="不欠费请填0"/>                                </div>                                <div class="form-group">                                    <label class="col-sm-1"><label for="phone">电费欠费</label></label>                                    <input class="col-sm-2" size="20" name="electric_price" type="text" placeholder="不欠费请填0"/>                                </div>                                <div class="form-group">                                    <label class="col-sm-1"><label for="phone">燃气欠费</label></label>                                    <input class="col-sm-2" size="20" name="gas_price" type="text" placeholder="不欠费请填0"/>                                </div>                                <div class="form-group">                                    <label class="col-sm-1"><label for="phone">停车欠费</label></label>                                    <input class="col-sm-2" size="20" name="park_price" type="text" placeholder="不欠费请填0"/>                                </div>                                <div class="form-group">                                    <label class="col-sm-1"><label for="phone">物业欠费</label></label>                                    <input class="col-sm-2" size="20" name="property_price" type="text" placeholder="不欠费请填0"/>                                </div>							</div>						</div>						<div class="space"></div>							<div class="clearfix form-actions">								<div class="col-md-offset-3 col-md-9">									<button class="btn btn-info" type="submit">										<i class="ace-icon fa fa-check bigger-110"></i>										保存									</button>								</div>							</div>						</div>					</form>				</div>			</div>		</div>	</div></div><style>.BMap_cpyCtrl{display:none;}input.ke-input-text {background-color: #FFFFFF;background-color: #FFFFFF!important;font-family: "sans serif",tahoma,verdana,helvetica;font-size: 12px;line-height: 24px;height: 24px;padding: 2px 4px;border-color: #848484 #E0E0E0 #E0E0E0 #848484;border-style: solid;border-width: 1px;display: -moz-inline-stack;display: inline-block;vertical-align: middle;zoom: 1;}.form-group>label{font-size:12px;line-height:24px;}#upload_pic_box{margin-top:20px;height:150px;}#upload_pic_box .upload_pic_li{width:130px;float:left;list-style:none;}#upload_pic_box img{width:100px;height:70px;border:1px solid #ccc;}</style><script src="{pigcms{$static_path}js/add_bindUser.js" type="text/javascript"></script><include file="Public:footer"/>