<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<foreach name="activity_list" item="v">					<a href="{pigcms{:U('Activity/hotactivity',array('activity_id'=>$v['activity_id']))}" class="off">{pigcms{$v.name}</a>					</foreach>				</ul>			</div>			<if condition="$Think.get.activity_id eq 3">			<form name="myform" id="myform" action="" method="post">				<h2>从活动开始到现在为止一共有{pigcms{$count_access}人参加活动</h2>				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>						</colgroup>						<thead>							<tr>								<th>用户编号</th>								<th>昵称</th>								<th>性别</th>								<th>电话号码</th>								<th>所在地</th>								<th>参加活动时间</th>							</tr>						</thead>						<tbody>							<if condition="is_array($activity_preson_list)">								<volist name="activity_preson_list" id="vo">									<tr>										<td>{pigcms{$vo.uid}</td>										<td>{pigcms{$vo.nickname}</td>										<td><if condition="$vo.sex eq 1">男</if><if condition="$vo.sex eq 2">女</if></td>										<td>{pigcms{$vo.phone}</td>										<td>{pigcms{$vo.city}</td>										<td>{pigcms{$vo.order_time|date='Y-m-d H:i',###}</td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="6">{pigcms{$get_log_list}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="8">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>				<elseif condition="$Think.get.activity_id eq 5"/>				<form name="myform" id="myform" action="" method="post">					<h2>从活动开始到现在为止一共有{pigcms{$count_access}人参加活动，其中一共有{pigcms{$gift_count['count_person_cash']}人获取现金红包，共{pigcms{$gift_count['sum_money']['money']/100}元，{pigcms{$gift_count['count_person_coupon']}人获得了大头仔现金券，{pigcms{$gift_count['count_person_coupon_car']}人获得了停车优惠券</h2>					<div class="table-list">						<table width="100%" cellspacing="0">							<colgroup>								<col/>								<col/>								<col/>								<col/>								<col/>								<col/>							</colgroup>							<thead>							<tr>								<th>用户编号</th>								<th>昵称</th>								<th>获得的奖品</th>								<th>奖品类型</th>								<th>奖品内容</th>								<th>参加活动时间</th>							</tr>							</thead>							<tbody>							<if condition="is_array($activity_preson_list_open)">								<volist name="activity_preson_list_open" id="vo">									<tr>										<td>{pigcms{$vo.uid}</td>										<td>{pigcms{$vo.nickname}</td>										<td>{pigcms{$vo.giftname}</td>										<td><if condition="$vo.giftname eq '现金红包'">现金</if><if condition="$vo.giftname eq '大头仔现金券'">优惠券</if><if condition="$vo.giftname eq '停车优惠券'">停车券</if></td>										<td><if condition="$vo.giftname eq '现金红包'">现金：{pigcms{$vo['number']/100}元</if><if condition="$vo.giftname eq '大头仔现金券'">优惠券SN：{pigcms{$vo.number}</if><if condition="$vo.giftname eq '停车优惠券'">使用车牌：{pigcms{$vo.plate}</if></td>										<td>{pigcms{$vo.time|date='Y-m-d H:i',###}</td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="6">{pigcms{$get_log_list}</td></tr>								<else/>								<tr><td class="textcenter red" colspan="8">列表为空！</td></tr>							</if>							</tbody>						</table>					</div>				</form>				<else/>				<table>					<tr>						<td class="textcenter red" colspan="8">列表为空！</td>					</tr>				</table>				</if>		</div><include file="Public:footer"/>