<include file="Public:header"/><link href="/tpl/Wap/default/static/webuploader-0.1.5/xb-webuploader.css" rel="stylesheet" type="text/css"><link href="http://fex.baidu.com/webuploader/css/demo.css" rel="stylesheet" type="text/css"><style>    .queueList{        padding:0 !important;        margin:0 !important;        border:0 !important;        margin-top:10px !important;    }</style><form id="myform" method="post" action="{pigcms{:U('Classify/infoedit_act')}" frame="true" refresh="true">		<table cellpadding="0" cellspacing="0" class="frame_form" width="100%">			<tr>				<th width="15%">标题</th>				<td><input class="input" type="text" name="title" value="{pigcms{$detail['title']}"></td>			</tr>			<tr>				<th width="15%">联系人</th>				<td><input class="input" type="text" name="lxname" value="{pigcms{$detail['lxname']}"></td>			</tr>			<tr>				<th width="15%">联系人电话</th>				<td >                    <if condition="strpos($detail['lxtel'], 'load/telimages')">                        <img src="{pigcms{$config['site_url']}/{pigcms{$detail['lxtel']}">                        <else/>                       <input class="input" type="text" name="lxtel" value="{pigcms{$detail['lxtel']}">                    </if>                </td>			</tr>			<if condition="!empty($content)">	        <volist name="content" id="vv">			<tr>				<th width="15%">{pigcms{$vv['tn']}<input type="hidden" class="input" name="k[]" value="{pigcms{$vv['tn']}"></th><!--				<if condition="is_array($vv['vv'])">--><!----><!--				    <td><input type="text" class="input" name="v[]" value="{pigcms{$vv['vv']|implode=','}"></td>--><!----><!--				<elseif condition="$vv['type'] eq 1 AND isset($vv['unit']) AND !empty($vv['unit'])"/>--><!----><!--				    <td><input type="text" class="input" name="v[]" value="{pigcms{$vv['vv']} / {pigcms{$vv['unit']}"></td>--><!----><!--				<elseif condition="$vv['type'] eq 5  AND !empty($vv['vv'])"/>--><!----><!--				    <td><input type="text" class="input" name="v[]" value="{pigcms{$vv['vv']|htmlspecialchars_decode=ENT_QUOTES}"></td>--><!----><!--				<else/>--><!--                    <td ><input type="text" class="input" name="v[]"  value="{pigcms{$vv['vv']}"></td>--><!--                </if>-->                <td>{pigcms{$vv['html']}</td>			</tr>			</volist>			</if>			<if condition="!empty($detail['otherdesc'])">			<tr>				<th width="15%">职位描述</th>				<php>$detail['otherdesc']=htmlspecialchars_decode($detail['otherdesc'],ENT_QUOTES);</php>				<td ><input type="text" class="input" name="otherdesc" value="{pigcms{$detail['otherdesc']|htmlspecialchars_decode=ENT_QUOTES}"></td>			</tr>			</if>			<if condition="!empty($detail['description'])">			<tr>				<th width="15%">说明描述</th>				<php>$detail['description']=htmlspecialchars_decode($detail['description'],ENT_QUOTES);</php>				<td ><input type="text" class="input" name="description"  value="{pigcms{$detail['description']|htmlspecialchars_decode=ENT_QUOTES}"></td>			</tr>			</if>		</table>		<if condition="!empty($imglist)">		<table cellpadding="0" cellspacing="0" class="frame_form" width="100%">		    <tr><!--				<td width="100">已上传的图片：</td>-->			</tr>           <php>$imgarr=array();</php>		  <volist name="imglist" id="vv" key="k">            <php>$imgarr[$k-1]['img']=$vv</php>		    <php>if($mod==0)echo "<tr>";</php><!--			<td><img class="view_msg" style="width:300px;" src="{pigcms{$vv}"></td>-->			<php>if($mod==1)echo "</tr>";</php>		 </volist>            <tr>                <td>                    <div id="uploader" class="xb-uploader">                       <div id="add_button">上传图片</div>                        <div class="queueList">                            <ul class="filelist">                            </ul>                        </div>                        <div class="statusBar" style="display:none;">                            <div class="progress" style="display: none;">                                <span class="text">0%</span>                                <span class="percentage" style="width: 0%;"></span>                            </div>                            <div class="info">共0个（0B），已上传0个</div>                        </div><!--                        <div class="btb uploadBtn state-pedding">提交信息</div>-->                    </div>                </td>            </tr>		</table>        <else />            <table cellpadding="0" cellspacing="0" class="frame_form" width="100%">                <tr>                    <td>                        <div id="uploader" class="xb-uploader">                            <div id="add_button">编辑图片</div>                            <div class="queueList">                            </div>                            <div class="statusBar" style="display:none;">                                <div class="progress" style="display: none;">                                    <span class="text">0%</span>                                    <span class="percentage" style="width: 0%;"></span>                                </div>                                <div class="info">共0个（0B），已上传0个</div>                            </div>                            <!--                        <div class="btb uploadBtn state-pedding">提交信息</div>-->                        </div>                    </td>                </tr>            </table>        </if>		<div class="btn hidden">		     <input type="hidden" name="vid" value="{pigcms{$vid}"/>			 <input type="button" name="dosubmit" class="btb uploadBtn" id="dosubmit" class="button" />		</div></form><script src="//cdn.bootcss.com/jquery-weui/1.0.0/js/jquery-weui.min.js"></script><script src="//cdn.staticfile.org/webuploader/0.1.5/webuploader.min.js"></script><script src="http://www.hdhsmart.com/tpl/Wap/default/static/js/upload.js"></script><script>    //图片上传    bd.bd_upload ({        upload_id:"#uploader",        thumbnailHeight:600,        thumbnailWidth:600,        url:'{pigcms{:U("ajax_upload")}',        marry_list:JSON.parse('{pigcms{:json_encode($imgarr)}'),        upload_finished_callback:function(){            //上传完成后提交表单            $('#uploader').parents('form').submit();        }    });    //没有图片时提交表单    $(document).on('click','.state-pedding',function(){        $('#uploader').parents('form').submit();    });    //查看大图    $(document).on('click','.view_msg',function(){        window.top.art.dialog({            padding: 0,            title: '大图',            content: '<img src="'+$(this).attr('src')+'"/>',            lock: true        });    });</script><!--<script type="text/javascript">function toCheck(id){   if(confirm('您确定审核通过此项吗？')){    $.post("{pigcms{:U('Classify/toVerify')}",{vid:id},function(data){	  data=parseInt(data);	  if(!data){          //window.location.reload();		  window.main.location.reload();	   }     },'JSON');   }else{     return false;   }}</script>--><include file="Public:footer"/>