<include file="Public:header"/>	<form id="myform" method="post" action="{pigcms{:U('Hickey/hickey_modify')}">		<table cellpadding="0" cellspacing="0" class="frame_form" width="100%" id="table">			<tr>				<th width="80">接口名称</th>				<td><input type="text" class="input fl" name="hickey_name" id="cat_name" size="25" placeholder="" validate="required:true" tips=""/></td>			</tr>			<tr>				<th width="80">施行动作名称</th>				<td><input type="text" class="input fl" name="hick_action" id="cat_name" size="25" placeholder="" validate="required:true" tips=""/></td>			</tr>			<tr>				<th width="80">云平台名称</th>				<td>					<select id="terrace_name" name="terrace_id">						<option value="0">请选择</option>						<foreach name="terrace_array" item="sv">							<option value="{pigcms{$sv.pigcms_id}">{pigcms{$sv.terrace_name}</option>						</foreach>					</select>				</td>			</tr>						<tr>				<th width="80">接口基类名称</th>				<td><span id="terrace_class"></span></td>			</tr>			<tr>				<th width="80">本地调用方法</th>				<td><input type="text" class="input fl" id="hickey_control" name="hickey_control" size="25" placeholder="英文" validate="required:true" tips="只能使用英文或数字"/></td>			</tr>			<tr>				<th width="80">请求方式</th>				<td>					<select name="curl_type">						<option value="0">请选择</option>						<option value="1">PUT</option>						<option value="2">DELETE</option>						<option value="3">GET</option>						<option value="4">POST</option>					</select>				</td>			</tr>			<tr id="add_tr">				<th width="80">其他参数</th>				<td>					<input type="text" class="input fl" name="arguments[]" size="10" placeholder="参数"/>&nbsp;&nbsp;&nbsp;&nbsp;					<input type="text" class="input fl" name="arguments_value[]" size="10" placeholder="参数值"/>					<select name="arguments_type[]">						<option value="0">参数类型</option>						<option value="1">常量</option>						<option value="2">变量</option>					</select>				</td>			</tr>			<tr>				<td>操作</td>				<td><span id="mybutton">添加行</span>&nbsp;&nbsp;&nbsp;&nbsp;<span id="mybutton2">删除行</span></td>			</tr>			<script>				$(function(){					$("#mybutton").click(function(){						var tr="<tr><th width='80'>其他参数</th><td> <input type='text' class='input fl' name='arguments[]' size='10' placeholder='参数'/> <input type='text' class='input fl' name='arguments_value[]' size='10' placeholder='参数值'/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<select name='arguments_type[]'><option value='0'>参数类型</option><option value='1'>常量</option><option value='2'>变量</option></select></td></tr>";						$("#add_tr").after(tr);					});					$("#mybutton2").click(function(){						var td_length = $("#table tr").length;						var pot = td_length-2;						if(td_length>8){							$("#table tr:eq("+pot+")").remove();						}					});					$("#terrace_name").change(function(){						var terrace_name = $(this).find("option:selected").text();						$.ajax({							url: "{pigcms{:U('Hickey/seacrch_name')}",							type: "GET",							data: {'terrace_name': terrace_name},							success: function (res) {								$("#terrace_class").html(res);							}						});					});				});			</script>		</table>		<div class="btn hidden">			<input type="submit" id="dosubmit" value="提交" class="button" />			<input type="reset" value="取消" class="button" />		</div>	</form><include file="Public:footer"/>