<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<if condition="!empty($Hickey_array)">						<a href="{pigcms{:U('Hickey/index')}" class="on">接口列表</a>|						<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Hickey/hickey_add')}','添加接口',480,260,true,false,false,addbtn,'add',true);">添加接口</a>					<else/>						<a href="{pigcms{:U('Terrace/index')}">接口列表</a>|						<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Hickey/hickey_add',array('cat_fid'=>intval($_GET['cat_fid'])))}','添加接口',520,370,true,false,false,addbtn,'add',true);">添加接口</a>					</if>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col width="180" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>								<th>云平台</th>								<th>请求的方法</th>								<th>动作名称</th>								<th>接口地址</th>								<th>状态</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="!empty($Hickey_array)">								<volist name="Hickey_array" id="vo">									<tr>										<td>{pigcms{$vo.pigcms_id}</td>										<td>{pigcms{$vo.terrace_name}</td>										<td>{pigcms{$vo.hickey_control}</td>										<td>{pigcms{$vo.hick_action}</td>										<td>{pigcms{$vo.url}</td>										<td name="change_state"><if condition="$vo['is_use'] eq 1"><span style="color: green;cursor: pointer" title="点击关闭">启用</span><elseif condition="$vo['is_use'] eq 2"/><span style="color: red">待审核</span><else/><span style="color: red;cursor: pointer" title="点击启用">关闭</span></if></td>										<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('hickey_update',array('pigcms_id'=>$vo['pigcms_id']))}','编辑接口信息',480,<if condition="$vo['cat_fid']">240<else/>340</if>,true,false,false,editbtn,'edit',true);">编辑</a> | <a href="javascript:void(0);" class="delete_row" parameter="pigcms_id={pigcms{$vo.pigcms_id}" url="{pigcms{:U('Hickey/hickey_del',array('pigcms_id'=>$vo['pigcms_id']))}">删除</a></td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="8">{pigcms{$get_log_list}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="8">列表为空！</td></tr>							</if>						</tbody>					</table>					<script>						$(function(){							$("[name='change_state']").click(function(){								var pigcms_id = $(this).siblings(":first").text();								var is_use = $(this).text();								$.ajax({									url: "{pigcms{:U('Hickey/change_state')}",									type: "GET",									data: {'pigcms_id': pigcms_id,'is_use':is_use},									success: function (res) {										if(res == 1){											location.reload()										}else if(res ==2){											location.reload()										}else{											alert('改变失败');										}									}								});							});						});					</script>				</div>			</form>		</div><include file="Public:footer"/>