<include file="Public:header"/><div class="mainbox">	<div id="nav" class="mainnav_title">		<ul>			<a href="{pigcms{:U('Index/log_list')}" class="on">日志列表</a>		</ul>	</div>	<form name="myform" id="myform" action="" method="post">		<div class="table-list">			<table width="100%" cellspacing="0">				<colgroup><col> <col> <col> <col><col><col><col><col width="240" align="center"> </colgroup>				<thead>				<tr>					<th>编号</th>					<th>操作人</th>					<th>操作表</th>					<th>操作动作</th>					<th>操作时间</th>					<th>操作IP</th>					<th>通过的模块</th>					<th class="textcenter">通过的方法</th>					<th class="textcenter">操作</th>				</tr>				</thead>				<tbody>				<if condition="is_array($log_arr)">					<volist name="log_arr" id="vo">						<tr>							<td>{pigcms{$vo.pigcms_id}</td>							<td>{pigcms{$vo.adminuser}</td>							<td>{pigcms{$vo.table_name}</td>							<td>{pigcms{$vo.opera_name}</td>							<td>{pigcms{$vo.do_time|date='Y-m-d H:i',###}</td>							<td>{pigcms{$vo.do_ip}</td>							<td>{pigcms{$vo.module_name}</td>							<td class="textcenter">{pigcms{$vo.action_name}</td>							<td class="textcenter">								<a href="javascript:void(0);" class="delete_row" parameter="pigcms_id={pigcms{$vo.pigcms_id}" url="{pigcms{:U('Index/log_del',array('pigcms_id'=>$vo['pigcms_id']))}">删除</a>							</td>						</tr>					</volist>					<tr><td class="textcenter pagebar" colspan="10">{pigcms{$pagebar}</td></tr>					<else/>					<tr><td class="textcenter red" colspan="10">列表为空！</td></tr>				</if>				</tbody>			</table>		</div>	</form></div><include file="Public:footer"/>