<include file="Public:header"/>	<form id="myform" method="post" action="{pigcms{:U('Merchant/modify')}" frame="true" refresh="true">		<table cellpadding="0" cellspacing="0" class="frame_form" width="100%">			<tr>				<th width="80">商户帐号</th>				<td><input type="text" class="input fl" name="account" size="25" placeholder="商户平台的帐号" validate="maxlength:20,required:true" tips="设定之后，以后不能再修改！"/></td>			</tr>			<tr>				<th width="80">商户密码</th>				<td><input type="password" id="check_pwd" check_width="180" class="input fl" name="pwd" size="25" placeholder="商户平台的密码" validate="required:true,minlength:6" tips="商户的密码很重要，填写难度较强的密码有效保护商户的信息，也可以保护网站的数据安全。"/></td>			</tr>			<tr>				<th width="80">商户名称</th>				<td><input type="text" class="input fl" name="name" size="25" placeholder="商户的公司名或..." validate="maxlength:20,required:true"/></td>			</tr>			<tr>				<th width="80">联系电话</th>				<td><input type="text" class="input fl" name="phone" size="25" placeholder="联系人的电话" validate="required:true" tips="多个电话号码以空格分开"/></td>			</tr>			<tr>				<th width="80">联系邮箱</th>				<td><input type="text" class="input fl" name="email" size="25" placeholder="可不填写" validate="email:true" tips="只供管理员后台记录，前台不显示"/></td>			</tr>            <tr>                <th width="80">所属社区</th>                <td>                    <select name="village_id" id="village_id">                        <option value="0" selected="selected">请选择</option>                        <volist name="village_result" id="vo">                            <option value="{pigcms{$vo.village_id}" >{pigcms{$vo.village_name}</option>                        </volist>                    </select>                </td>            </tr>            <tr id="project_id_list" style="display: none;">                <th width="80">所属期数</th>                <td>                    <select name="project_id" id="project_id">                                            </select>                </td>            </tr>			<tr>				<th width="80">商户状态</th>				<td>					<span class="cb-enable"><label class="cb-enable selected"><span>启用</span><input type="radio" name="status" value="1" checked="checked" /></label></span>					<span class="cb-disable"><label class="cb-disable"><span>关闭</span><input type="radio" name="status" value="0" /></label></span>				</td>			</tr>			<tr>				<th width="80">签约商家</th>				<td>					<span class="cb-enable"><label class="cb-enable"><span>是</span><input type="radio" name="issign" value="1"/></label></span>					<span class="cb-disable"><label class="cb-disable  selected"><span>否</span><input type="radio" name="issign" value="0" checked="checked" /></label></span>				</td>			</tr>			<tr>				<th width="80">认证商家</th>				<td>					<span class="cb-enable"><label class="cb-enable"><span>是</span><input type="radio" name="isverify" value="1" /></label></span>					<span class="cb-disable"><label class="cb-disable  selected"><span>否</span><input type="radio" name="isverify" value="0"  checked="checked"/></label></span>				</td>			</tr>			<tr>				<th width="80">使用公众号</th>				<td>					<span class="cb-enable"><label class="cb-enable selected"><span>允许</span><input type="radio" name="is_open_oauth" value="1" checked="checked" /></label></span>					<span class="cb-disable"><label class="cb-disable"><span>禁止</span><input type="radio" name="is_open_oauth" value="0"/></label></span>					<em class="notice_tips" tips="如果系统设置中允许所有商家都使用公众号，请禁止无效。"></em>				</td>			</tr>			<tr>				<th width="80">开微店</th>				<td>					<span class="cb-enable"><label class="cb-enable selected"><span>允许</span><input type="radio" name="is_open_oauth" value="1" checked="checked" /></label></span>					<span class="cb-disable"><label class="cb-disable"><span>禁止</span><input type="radio" name="is_open_oauth" value="0"/></label></span>					<em class="notice_tips" tips="如果系统设置中允许所有商家都能开微店，请禁止无效。"></em>				</td>			</tr>		</table>		<div class="btn hidden">			<input type="submit" name="dosubmit" id="dosubmit" value="提交" class="button" />			<input type="reset" value="取消" class="button" />		</div>	</form>    <script>        $('#village_id').change(function () {            var p1=$(this).children('option:selected').val();            $.ajax({                type:"POST",                url:'{pigcms{:U('ajax_village_info')}',                data:{village_id:p1},                success:function(re) {                    re=JSON.parse(re);                    console.log(typeof re);                    if(re.res=='1'){                        $('#project_id_list').css('display','');                        $('#project_id').empty();                        $.each(re.data,function(idx, obj){                            $('#project_id').append("<option value='"+obj.pigcms_id+"'>"+obj.desc+"</option>");                        });                    }else{                        $('#project_id_list').css('display','none');                    }                }            });            }        )    </script><include file="Public:footer"/>