<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<if condition="!empty($terrace_array)">						<a href="{pigcms{:U('Terrace/index')}" class="on">平台列表</a>|						<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Terrace/terrace_add')}','添加平台',480,260,true,false,false,addbtn,'add',true);">添加平台</a>					<else/>						<a href="{pigcms{:U('Terrace/index')}">平台列表</a>|						<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Appoint/cat_add',array('cat_fid'=>intval($_GET['cat_fid'])))}','添加子分类',520,370,true,false,false,addbtn,'add',true);">添加平台</a>					</if>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col width="180" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>								<th>云平台名称</th>								<th>接口基础地址</th>								<th>请求格式</th>								<th>API KEY</th>								<th>状态</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="!empty($terrace_array)">								<volist name="terrace_array" id="vo">									<tr>										<td>{pigcms{$vo.pigcms_id}</td>										<td>{pigcms{$vo.terrace_name}</td>										<td>{pigcms{$vo.url}</td>										<td><if condition="$vo['data_type'] eq 1">JSON</if><if condition="$vo['data_type'] eq 2">XML</if></td>										<td>{pigcms{$vo.key}</td>										<td name="change_state"><if condition="$vo['is_use'] eq 1"><font color="green">启用</font><elseif condition="$vo['is_use'] eq 2"/><font color="red">待审核</font><else/><font color="red">关闭</font></if></td>										<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('terrace_update',array('pigcms_id'=>$vo['pigcms_id']))}','编辑平台信息',480,<if condition="$vo['cat_fid']">240<else/>340</if>,true,false,false,editbtn,'edit',true);">编辑</a> | <a href="javascript:void(0);" class="delete_row" parameter="pigcms_id={pigcms{$vo.pigcms_id}" url="{pigcms{:U('Terrace/terrace_del',array('pigcms_id'=>$vo['pigcms_id']))}">删除</a></td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="7">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="7">列表为空！</td></tr>							</if>						</tbody>					</table>					<script>						$(function(){							$("[name='change_state']").click(function(){								var pigcms_id = $(this).siblings(":first").text();								var is_use = $(this).text();								$.ajax({									url: "{pigcms{:U('Terrace/change_state')}",									type: "GET",									data: {'pigcms_id': pigcms_id,'is_use':is_use},									success: function (res) {										if(res == 1){											alert("警告！将关闭所有平台！");											location.reload()										}else if(res ==2){											location.reload()										}else{											alert('改变失败');										}									}								});							});						});					</script>				</div>			</form>		</div><include file="Public:footer"/>