<include file="Public:header"/>	<form id="myform" method="post" action="{pigcms{:U('Terrace/terrace_modify')}">		<table cellpadding="0" cellspacing="0" class="frame_form" width="100%" id="table">			<tr>				<th width="80">云平台名称</th>				<td><input type="text" class="input fl" name="terrace_name" id="cat_name" size="25" placeholder="" validate="required:true" tips=""/></td>			</tr>			<tr>				<th width="80">接口地址</th>				<td><input type="text" class="input fl" name="url" id="cat_url" size="25" placeholder="英文或数字" validate="required:true" tips="只能使用英文或数字"/></td>			</tr>			<tr>				<th width="80">接口类名称</th>				<td><input type="text" class="input fl" name="terrace_class" id="cat_url" size="25" placeholder="英文" validate="required:true" tips="只能使用英文或数字"/></td>			</tr>			<tr>				<th width="80">传参格式</th>				<td>					<select name="data_type">						<option value="0">请选择</option>						<option value="1">JSON</option>						<option value="2">XML</option>					</select>				</td>			</tr>			<tr>				<th width="80">API KEY</th>				<td>					<input type="text" class="input fl" name="key"  size="10" placeholder="API KEY" validate="required:true" tips="务必填写正确的key"/>				</td>			</tr>			<tr name="add_tr">				<th width="80">其他参数</th>				<td>					<input type="text" class="input fl" name="arguments[]" size="10" placeholder="参数"/>&nbsp;&nbsp;&nbsp;&nbsp;					<input type="text" class="input fl" name="arguments_value[]" size="10" placeholder="参数值"/>				</td>			</tr>			<tr>				<td>操作</td>				<td><span id="mybutton">添加行</span>&nbsp;&nbsp;&nbsp;&nbsp;<span id="mybutton2">删除行</span></td>			</tr>			<script>				$(function(){					$("#mybutton").click(function(){						var tr="<tr name='add_tr'><th width='80'>其他参数</th><td> <input type='text' class='input fl' name='arguments[]' size='10' placeholder='参数'/> <input type='text' class='input fl' name='arguments_value[]' size='10' placeholder='参数值'/></td></tr>";						$("*[name='add_tr']:last").after(tr);					});					$("#mybutton2").click(function(){						var td_length = $("#table tr").length;						var pot = td_length-2;						if(td_length>7){							$("#table tr:eq("+pot+")").remove();						}					});				});			</script>		</table>		<div class="btn hidden">			<input type="submit" id="dosubmit" value="提交" class="button" />			<input type="reset" value="取消" class="button" />		</div>	</form><include file="Public:footer"/>