<include file="Public:header"/>	<form id="myform" method="post" action="{pigcms{:U('Terrace/terrace_edit')}">		<input type="hidden" name="pigcms_id" value="{pigcms{$terrace_array.pigcms_id}"/>		<table cellpadding="0" cellspacing="0" class="frame_form" width="100%" id="table">			<tr>				<th width="80">云平台名称</th>				<td><input type="text" class="input fl" name="terrace_name" id="cat_name" size="25" value="{pigcms{$terrace_array.terrace_name}" validate="required:true" tips=""/></td>			</tr>			<tr>				<th width="80">接口地址</th>				<td><input type="text" class="input fl" name="url" id="cat_url" size="25" value="{pigcms{$terrace_array.url}" validate="required:true" tips="只能使用英文或数字"/></td>			</tr>			<tr>				<th width="80">接口类名称</th>				<td><input type="text" class="input fl" name="terrace_class" id="cat_url" size="25" value="{pigcms{$terrace_array.terrace_class}" validate="required:true" tips="只能使用英文或数字"/></td>			</tr>			<tr>				<th width="80">传参格式</th>				<td>					<select name="data_type">						<option value="0"<if condition="$terrace_array['data_type'] eq 0">selected='selected'<else/></if>>请选择</option>						<option value="1"<if condition="$terrace_array['data_type'] eq 1">selected='selected'<else/></if>>JSON</option>						<option value="2"<if condition="$terrace_array['data_type'] eq 2">selected='selected'<else/></if>>XML</option>					</select>				</td>			</tr>			<tr>				<th width="80">API KEY</th>				<td>					<input type="text" class="input fl" name="key"  size="10" value="{pigcms{$terrace_array.key}" validate="required:true" tips="务必填写正确的key"/>				</td>			</tr>			<tr>				<th width="80">是否启用</th>				<td>					<span class="cb-enable"><label class="cb-enable <if condition="$terrace_array['is_use'] eq 1">selected</if>"><span>是</span><input type="radio" name="is_use" value="1" <if condition="$terrace_array['is_use'] eq 1">selected='selected'</if>/></label></span>					<span class="cb-disable"><label class="cb-disable <if condition="$terrace_array['is_use'] eq 0">selected</if>""><span>否</span><input type="radio" name="is_use" value="0"<if condition="$terrace_array['is_use'] eq 0">selected='selected'</if>/></label></span>					<em class="notice_tips" tips="如果选择启用，颜色会有变化"></em>				</td>			</tr>			<foreach name="arguments_all_feild" item="sv">			<tr name="add_tr">				<th width="80">其他参数</th>				<td>					<input type="text" class="input fl" name="arguments[]" size="10" value="{pigcms{$sv.arguments}"/>&nbsp;&nbsp;&nbsp;&nbsp;					<input type="text" class="input fl" name="arguments_value[]" size="10" value="{pigcms{$sv.arguments_value}"/>				</td>			</tr>			</foreach>			<tr>				<td>操作</td>				<td><span id="mybutton">添加行</span>&nbsp;&nbsp;&nbsp;&nbsp;<span id="mybutton2">删除行</span></td>			</tr>			<script>				$(function(){					$("#mybutton").click(function(){						var tr="<tr name='add_tr'><th width='80'>其他参数</th><td> <input type='text' class='input fl' name='arguments[]' size='10' placeholder='参数'/> <input type='text' class='input fl' name='arguments_value[]' size='10' placeholder='参数值'/></td></tr>";						$("*[name='add_tr']:last").after(tr);					});					$("#mybutton2").click(function(){						var is_null = $("input[name='arguments[]']:last").val();						var td_length = $("#table tr").length;						var pot = td_length-2;						if(is_null == ''){							$("#table tr:eq("+pot+")").remove();						}					});				});			</script>		</table>		<div class="btn hidden">			<input type="submit" id="dosubmit" value="提交" class="button" />			<input type="reset" value="取消" class="button" />		</div>	</form><include file="Public:footer"/>