<!--头部--><include file="Public:top"/><!--头部结束--><body>	<header class="pigcms-header mm-slideout">		<a href="#slide_menu" id="pigcms-header-left">			<i class="iconfont icon-menu "></i>		  </a>	      <p id="pigcms-header-title">{pigcms{$config.group_alias_name}商品管理</p>		<a  href="{pigcms{:U('Index/group_add')}" id="pigcms-header-right">添加商品</a>		</header>	<div class="container container-fill">		<!--左侧菜单-->		<include file="Public:leftMenu"/>		<!--左侧菜单结束--><link rel="stylesheet" href="{pigcms{$static_path}css/shop_item.css"><script type="text/javascript" src="{pigcms{$static_path}js/iscroll.js"></script>	<div class="item-list-wrap">		<div id="item-list-wrapper" class="pigcms-main" style="">			<div id="item-list-scroller">				<ul id="item-list-ul" style="margin-bottom: 70px;">					<div class="pigcms-container">						<div class="search-container">							<div id="input-wrap"></div>							<i class="iconfont icon-search"></i>							<input type="text" class="pigcms-search" name="keyword" placeholder="商品名称">						</div>						<div class="header-fliter-container"></div>					</div>					<div id="item-list-div">					</div>				</ul>			</div>		</div>		<div class="item-list-footer">			<a href="{pigcms{:U('Index/mpackageadd')}" class="footer-operation">				<i class="iconfont icon-add"></i><span>添加套餐</span>			</a>			<a href="{pigcms{:U('Index/group_add')}" class="footer-operation">				<i class="iconfont icon-add"></i><span>添加商品</span>			</a>		</div>	</div></body><script>    var staticpath="{pigcms{$static_path}";	var url = "{pigcms{:U('Index/gpro')}",		can_manage = 1;		$(function(){			$(".pigcms-main").css('height', $(window).height()-50);		})		var myScroll_fliter, edit_url = "";	</script>	<script src="{pigcms{$static_path}js/shop_item.js"></script>		<include file="Public:footer"/></html>